import React, {Component} from 'react';
import {  View, Text, StyleSheet } from 'react-native';

class Layout1 extends Component {
    constructor(){
      super();
      this.state = {
        provinsi: [],
        refreshing: false
      }
    }
render (){
    return (
        <View style={{flexDirection: 'row', justifyContent: 'center'}}>

            <View style={{
            height: 70,
            padding:10,
            width: '25%',
            shadowColor: '#000',
            shadowOpacity:0.2,
            shadowRadius:1,
            backgroundColor:'#f6d743',
            borderRadius:10
            }}>
            <Text style={{ textAlign: 'center', fontWeight: 'bold'}}>Positif</Text>
            </View>

            <View style={{
            height: 70,
            padding:10,
            marginLeft: '5%',
            width: '25%',
            shadowColor: '#000',
            shadowOpacity:0.2,
            shadowRadius:1,
            backgroundColor:'#42e6a4',
            borderRadius:10
            }}>
            <Text style={{ textAlign: 'center', fontWeight: 'bold'}}>Sembuh</Text>
            </View>

            <View style={{ 
            height: 70,
            padding:10,
            marginBottom:0,
            marginLeft: '5%',
            width: '25%',
            shadowColor: '#000',
            shadowOpacity:0.2,
            shadowRadius:1,
            backgroundColor:'#eb4559',
            borderRadius:10
             }}>
            <Text style={{ textAlign: 'center', fontWeight: 'bold'}}>Meninggal</Text>
            </View>


        </View>
    )
}
    renderItem = ({ item }) => (
        <View style={{padding: 10, borderColor: "#000",borderWidth: 1, backgroundColor: '#ffffff', borderRadius:10, flexDirection: 'row', justifyContent:'space-between' }}>
          <Text style={{fontWeight: 'bold'}}>{item.provinsi}</Text>
          <View style={{ 
            fontWeight: 'bold',
            padding:1,
            height:25,
            width:50,
            backgroundColor:'#f6d743',
            borderRadius:10,
            flexDirection: 'row',
            justifyContent: 'center'
            }}>
          <Text>{item.kasusPosi}</Text>
          </View>
    
          <View style={{ 
            fontWeight: 'bold',
            padding:1,
            height:25,
            width:50,
            backgroundColor:'#42e6a4',
            borderRadius:10,
            flexDirection: 'row',
            justifyContent: 'center'
            }}>
          <Text>{item.kasusSemb}</Text>
          </View>
    
          <View style={{ 
            fontWeight: 'bold',
            padding:1,
            height:25,
            width:50,
            backgroundColor:'#eb4559',
            borderRadius:10,
            flexDirection: 'row',
            justifyContent: 'center'
            }}>
          <Text>{item.kasusMeni}</Text>
        </View>
        </View>
      )
    
      onRefresh = () => {
        this.getDataApi
      }
    
      componentDidMount = () => {
        this.getDataApi();
      }
      
      getDataApi = async () => {
        this.setState({ refreshing: true })
        // fetch('https://indonesia-covid-19.mathdro.id/api/provinsi')
        //       .then (response => response.json())
        //       .then(json => this.setState({ Indonesia: json, refreshing: false}))
    
        const response = await fetch ( 'https://indonesia-covid-19.mathdro.id/api/provinsi');
        const json = await response.json();
        this.setState({ provinsi: json.data,refreshing: false})
    
      } 

}
export default Layout1;
