import React, { Component } from 'react';
import {
  FlatList,
  StyleSheet,
  Text,
  StatusBar,
  View,
  ScrollView,
} from 'react-native';

class Card extends Component {
  constructor(){
    super();
    this.state = {
      provinsi: [],
      refreshing: false
    }
  }

  renderItem = ({ item }) => (
    <View style={{ padding: 20, shadowColor:"000" , backgroundColor: '#ffffff', borderRadius:5}}>
      <Text>{item.provinsi}</Text>
    </View>
  )

  onRefresh = () => {
    this.getDataApi
  }

  componentDidMount = () => {
    this.getDataApi();
    StatusBar.setHidden(true);
  }
  
  getDataApi = async () => {
    this.setState({ refreshing: true })
    // fetch('https://indonesia-covid-19.mathdro.id/api/provinsi')
    //       .then (response => response.json())
    //       .then(json => this.setState({ Indonesia: json, refreshing: false}))

    const response = await fetch ( 'https://indonesia-covid-19.mathdro.id/api/provinsi');
    const json = await response.json();
    this.setState({ provinsi: json.data, refreshing: false})

  } 

  render() {
     return(
      <View>
        <ScrollView>
        <View style={{ backgroundColor: '#ffffff', paddingVertical: 10}}>
          <Text style={{textAlign: 'center', fontWeight: 'bold', fontSize:18 }}>Indonesia</Text> 
        </View>
        <FlatList
            data={this.state.provinsi}
            // keyExtractor={item => item.id.toString()}
            renderItem={this.renderItem}
            refreshing={this.state.refreshing}
            onRefresh={this.onRefresh}
        />
        </ScrollView>
      </View> 
    )
  }
};

export default Card;
