import React, { Component } from 'react';
import {
  FlatList,
  StyleSheet,
  Text,
  StatusBar,
  View,
  ScrollView,
} from 'react-native';

class Card extends Component {
  constructor(){
    super();
    this.state = {
      provinsi: [],
      refreshing: false
    }
  }

  render() {
    return(
     <View>
       
       <View style={{ backgroundColor: '#ffffff', paddingVertical: 10}}>
         <Text style={{textAlign: 'center', fontWeight: 'bold', fontSize:18 }}>Indonesia</Text> 
       </View>
     
       <FlatList
           data={this.state.provinsi}
           // keyExtractor={item => item.id.toString()}
           renderItem={this.renderItem}
           refreshing={this.state.refreshing}
           onRefresh={this.onRefresh}/>
      
     </View> 
   )
   
 }
  renderItem = ({ item }) => (
    <View style={{padding: 10, borderColor: "#000",borderWidth: 1, backgroundColor: '#ffffff', borderRadius:10,justifyContent:"flex-end", flexDirection: 'row', alignContent: 'flex-end'}}>
      <Text style={{fontWeight: 'bold'}}>{item.provinsi}</Text>
      <View style={{alignSelf:"flex-end"}}>
      <View style={{ 
        fontWeight: 'bold',
        padding:1,
        height:25,
        width:50,
        backgroundColor:'#f6d743',
        borderRadius:10,
        flexDirection: 'row',
        justifyContent: 'center',
        alignContent:'flex-end'
        }}>
      <Text>{item.kasusPosi}</Text>
      </View>
      </View>

      <View style={{ 
        fontWeight: 'bold',
        padding:1,
        height:25,
        width:50,
        backgroundColor:'#42e6a4',
        borderRadius:10,
        flexDirection: 'row',
        justifyContent: 'center',
        alignContent:'flex-end'
        }}>
      <Text>{item.kasusSemb}</Text>
      </View>

      <View style={{ 
        fontWeight: 'bold',
        padding:1,
        height:25,
        width:50,
        backgroundColor:'#eb4559',
        borderRadius:10,
        flexDirection: 'row',
        justifyContent: 'center',
        alignSelf:'flex-end'
        }}>
      <Text>{item.kasusMeni}</Text>
    </View>
    </View>
  )

  onRefresh = () => {
    this.getDataApi
  }

  componentDidMount = () => {
    this.getDataApi();
    StatusBar.setHidden(true);
  }
  
  getDataApi = async () => {
    this.setState({ refreshing: true })
    // fetch('https://indonesia-covid-19.mathdro.id/api/provinsi')
    //       .then (response => response.json())
    //       .then(json => this.setState({ Indonesia: json, refreshing: false}))

    const response = await fetch ( 'https://indonesia-covid-19.mathdro.id/api/provinsi');
    const json = await response.json();
    this.setState({ provinsi: json.data,refreshing: false})

  } 

};

export default Card;
