import React, {Component} from 'react';
import {  View, Text, StyleSheet, FlatList } from 'react-native';

class Layout2 extends Component {
    constructor(){
      super();
      this.state = {
        positif: [],
        sembuh: [],
        meninggal: [],
        refreshing: false
      }
    }

    render() {
        return(
         <View>
            <Text style={{fontWeight:"bold", fontSize: 18, textAlign:"center"}}>Global</Text>
           <FlatList
               data={[this.state.positif], [this.state.sembuh], [this.state.meninggal]}
              //  keyExtractor={item => item.global.toString()}
               renderItem={this.renderItem}
               refreshing={this.state.refreshing}
               onRefresh={this.onRefresh}/>


            <View style={{flexDirection: 'row', justifyContent: 'center'}}>
            <View style={{
            height: 70,
            padding:10,
            width: '25%',
            shadowColor: '#000',
            shadowOpacity:0.2,
            shadowRadius:1,
            backgroundColor:'#f6d743',
            borderRadius:10,
            justifyContent: "center"
            }}>
            <Text style={{ textAlign: 'center', fontWeight: 'bold'}}>Positif</Text>
            <Text style={{ textAlign: 'center', fontWeight: 'bold'}}>{this.state.positif}</Text>
            
            </View>


            <View style={{
            height: 70,
            padding:10,
            marginLeft: '5%',
            width: '25%',
            shadowColor: '#000',
            shadowOpacity:0.2,
            shadowRadius:1,
            backgroundColor:'#42e6a4',
            borderRadius:10,
            justifyContent: "center"
            }}>
            <Text style={{ textAlign: 'center', fontWeight: 'bold'}}>Sembuh</Text>
            <Text style={{ textAlign: 'center', fontWeight: 'bold'}}>{this.state.sembuh}</Text>
            </View>

            <View style={{ 
            height: 70,
            padding:10,
            marginBottom:0,
            marginLeft: '5%',
            width: '25%',
            shadowColor: '#000',
            shadowOpacity:0.2,
            shadowRadius:1,
            backgroundColor:'#eb4559',
            borderRadius:10,
            justifyContent: "center"
            }}>
          <Text style={{ textAlign: 'center', fontWeight: 'bold'}}>Meninggal</Text>
          <Text style={{ textAlign: 'center', fontWeight: 'bold'}}>{this.state.meninggal}</Text></View>
           </View>
         </View> 
       )
       
     }
    
     renderItem = ({ item }) => (
            <View>
              <Text>{item.positif}</Text>
            </View>
        )
    

    onRefresh = () => {
        this.getDataApi
      }
    
      componentDidMount = () => {
        this.getDataApi();
      }
      
      getDataApi = async () => {
        this.setState({ refreshing: true })
        const response1 = await fetch ('https://covid19.mathdro.id/api')
        const json = await response1.json()
        this.setState({ 
          positif: json.confirmed.value ,
          sembuh: json.recovered.value ,
          meninggal: json.deaths.value , refreshing: false})
      } 

};

export default Layout2;
