import React, {Component} from 'react';
import {  View, Text, StyleSheet,FlatList } from 'react-native';

class Layout1 extends Component {
    constructor(){
      super();
      this.state = {
        positif: [],
        sembuh: [],
        meninggal: [],
        refreshing: false
      }
    }

    render() {
      console.log(this.state.global)
        return(
         <View>
    
           <FlatList
              data={[this.state.positif], [this.state.sembuh], [this.state.meninggal]}
             //  keyExtractor={item => item.global.toString()}
              renderItem={this.renderItem}
              refreshing={this.state.refreshing}
              onRefresh={this.onRefresh}/>
              <Text>{this.state.positif}</Text>

           />
         </View> 
       )
       
     }
    
     renderItem = ({ item }) => (
            <View style={{flexDirection: 'row', justifyContent: 'center'}}>

            <View style={{
            height: 70,
            padding:10,
            width: '25%',
            shadowColor: '#000',
            shadowOpacity:0.2,
            shadowRadius:1,
            backgroundColor:'#f6d743',
            borderRadius:10,
            justifyContent: "center"
            }}>
            <Text style={{ textAlign: 'center', fontWeight: 'bold'}}> Positif </Text>
            <Text style={{ textAlign: 'center', fontWeight: 'bold'}}> {`${item.confirmed}`} </Text>
            </View>

            <View style={{
            height: 70,
            padding:10,
            marginLeft: '5%',
            width: '25%',
            shadowColor: '#000',
            shadowOpacity:0.2,
            shadowRadius:1,
            backgroundColor:'#42e6a4',
            borderRadius:10,
            justifyContent: "center"
            }}>
            <Text style={{ textAlign: 'center', fontWeight: 'bold'}}>Sembuh</Text>
            <Text style={{ textAlign: 'center', fontWeight: 'bold'}}>{`${item.value}`}</Text>
            </View>

            <View style={{ 
            height: 70,
            padding:10,
            marginBottom:0,
            marginLeft: '5%',
            width: '25%',
            shadowColor: '#000',
            shadowOpacity:0.2,
            shadowRadius:1,
            backgroundColor:'#eb4559',
            borderRadius:10,
            justifyContent: "center"
             }}>
            <Text style={{ textAlign: 'center', fontWeight: 'bold'}}>Meninggal</Text>
            <Text style={{ textAlign: 'center', fontWeight: 'bold'}}>206811</Text>
            </View>
        </View>
        )
    

    onRefresh = () => {
        this.getDataApi
      }
    
      componentDidMount = () => {
        alert("Covid-19 Tracking Project                              by : Glainhard Damping")
        this.getDataApi();
      }
      
      getDataApi = async () => {
        this.setState({ refreshing: true })
        const response = await fetch ('https://covid19.mathdro.id/api');
        const json = await response.json();
        this.setState({ global: json.confirmed.value ,refreshing: false})

      
    
      } 

};

export default Layout1;
