import React, {Component} from 'react';
import {  View, Text, StyleSheet } from 'react-native';

class Layout1 extends Component {
    constructor(){
      super();
      this.state = {
        provinsi: [],
        refreshing: false
      }
    }
renderItem = ({item}) => {
    return (
        <View style={{flexDirection: 'row', justifyContent: 'center'}}>

            <View style={{
            height: 70,
            padding:10,
            width: '25%',
            shadowColor: '#000',
            shadowOpacity:0.2,
            shadowRadius:1,
            backgroundColor:'#f6d743',
            borderRadius:10
            }}>
            <Text style={{ textAlign: 'center', fontWeight: 'bold'}}>Positif</Text>
            </View>

            <View style={{
            height: 70,
            padding:10,
            marginLeft: '5%',
            width: '25%',
            shadowColor: '#000',
            shadowOpacity:0.2,
            shadowRadius:1,
            backgroundColor:'#42e6a4',
            borderRadius:10
            }}>
            <Text style={{ textAlign: 'center', fontWeight: 'bold'}}>Sembuh</Text>
            </View>

            <View style={{ 
            height: 70,
            padding:10,
            marginBottom:0,
            marginLeft: '5%',
            width: '25%',
            shadowColor: '#000',
            shadowOpacity:0.2,
            shadowRadius:1,
            backgroundColor:'#eb4559',
            borderRadius:10
             }}>
            <Text style={{ textAlign: 'center', fontWeight: 'bold'}}>Meninggal</Text>
            </View>


        </View>
    )

    onRefresh = () => {
        this.getDataApi
      }
    
      componentDidMount = () => {
        this.getDataApi();
      }
      
      getDataApi = async () => {
        this.setState({ refreshing: true })
    
        const response = await fetch ( 'https://indonesia-covid-19.mathdro.id/api/provinsi');
        const json = await response.json();
        this.setState({ provinsi: json.data,refreshing: false})
    
      } 
    }
   

}
export default Layout1;
